func main() {
	//for each rune in the starting string that is equal to ( +1, ) -1 and work out what the final sum
	totalSum := 0
	targetSum := -1
	var targetPosition []int

	for i, r := range startingString {
		if r == '(' {
			totalSum++
		} else if r == ')' {
			totalSum--
		}
		if totalSum == targetSum {
			targetPosition = append(targetPosition, i)

		}
	}
	fmt.Println(len(startingString))
	fmt.Println(targetPosition[0])
	fmt.Println(totalSum)

	// Open the CSV file
	file, err := os.Open("input.csv")
	if err != nil {
		panic(err)
	}
	defer file.Close()

	reader := csv.NewReader(file)
	reader.FieldsPerRecord = -1 // Allow variable number of fields
	data, err := reader.ReadAll()
	if err != nil {
		panic(err)
	}

	totalArea := 0
	// i have got the data now, i need to use regex to separate them ->
	//get numbers before x, between the two xs and after the second x
	//for _, row := range data {
	//	for _, col := range row {
	//		fmt.Printf("%s,", col)
	//	}
	for _, row := range data {
		for _, col := range row {

			s := regexp.MustCompile("x").Split(col, 3)
			l, _ := strconv.Atoi(s[0])
			w, _ := strconv.Atoi(s[1])
			h, _ := strconv.Atoi(s[2])
			a1 := (l * w)
			a2 := (l * h)
			a3 := (w * h)
			m := min(a1, a2)
			m2 := min(m, a3)
			sum := (2 * l * w) + (2 * l * h) + (2 * w * h)
			totalArea += sum
			totalArea += m2
		}
	}
	fmt.Println(totalArea)
	fmt.Println(data[0])
	fmt.Println(data[0][0])
	s := regexp.MustCompile("x").Split(data[0][0], 3)
	l, _ := strconv.Atoi(s[0])
	w, _ := strconv.Atoi(s[1])
	h, _ := strconv.Atoi(s[2])
	a1 := (l * w)
	a2 := (l * h)
	a3 := (w * h)
	m := min(a1, a2)
	m2 := min(m, a3)

	fmt.Printf("%d,%d,%d", l, w, h)
	sum := (l * w) + (l * h) + (w * h)
	fmt.Println(sum + m2)
}
